import React, { useState,useEffect} from 'react';
// import axios from 'axios';
// import TextField from '@material-ui/core/TextField';
// import Autocomplete from '@material-ui/lab/Autocomplete';
import {WatchList} from './exportfiles'
function SearchSection() {
    const [watchvalue, setWatchvalue] = useState([{watchValues:''}]);

    useEffect(() => {
      fetch("http://localhost:5000/watchlist").then(res=>{
      if(res.ok){
        return res.json()
      }
    }).then(jsonRes=>setWatchvalue(jsonRes))
  })
    
  const [text, setText] = useState('');
  const [suggestion, setSuggestion] = useState([]);

    // const fetchData = () => {
    //   fetch("http://localhost:5000/watchlist")
    //     .then(response => {
    //       return response.json()
    //     })
    //     .then(data => {
    //       setWatchvalue(data)
    //     })
    // }
  
    // useEffect(() => {
    //   fetchData()
    // }, [])
 
// suggestion


  // onchnage

  const onChangeHandler=(text)=>{
    let matches=[]
    if(text.length>0){
      matches = watchvalue.filter(searchValue=>{
        const regex = new RegExp(`${text}`,"gi");
        return searchValue.watchValues.match(regex)
      })
    }
    console.log("matches", matches)
    setSuggestion(matches)
    setText(text);
  }
//  


  return (
    <>


    <div className="col-md-3" style={{marginLeft:"15px",marginTop:"15px"}}>
    <div className="input-group container" style={{
    display: "flex",
    margin: "0px",
    padding: "0px",
    lineHeight:"0"
}}>
        <div id="search-autocomplete" className="form-outline">
          <input type="search" id="form1" className="form-control" onChange={e=>onChangeHandler(e.target.value)} value={text} style={{paddingRight:"60px"}}/>
  
        </div>
        <button type="button" className="btn btn-primary">
          <i className="fas fa-search" />
        </button>
      
</div>
    <div className="my-3">
   {watchvalue.length > 0 && ( <ul className="list-group" style={{width: "84%"}}>
     {watchvalue.map(val => (
        <li key={val} className="list-group-item">{val.watchValues}</li>
         ))}
      </ul>
      )}

    </div>
<WatchList/>
      </div>
    
    </>
    );
}

export default SearchSection;
