import React, { useState,useEffect} from 'react';
import axios from 'axios';
// import TextField from '@material-ui/core/TextField';
// import Autocomplete from '@material-ui/lab/Autocomplete';
import {WatchList} from './exportfiles'
function SearchSection() {
   const [serach, setSerach] = useState([]);
   const [text, setText] = useState('')
   const [suggestion, setSuggestion] = useState('')
   useEffect(() => {
     const loadsearch = async ()=>{
       const response = await axios.get('https://reqres.in/api/users?')
     setSerach(response.data.data)
      }
      loadsearch()
     
   }, [])

   const onSuggestHandler=(text)=>{
     setText(text);
     setSuggestion([])
   }
   
   const onChangeHandler=(text)=>{
     let matches=[];
     if(text.length> 0){   
       matches=serach.filter(val=>{
         const regex = new RegExp(`${text}`,'gi');
         return val.email.match(regex)
       })
     }
     console.log('matches',matches)
setSuggestion(matches)
     setText(text)
   }
  return (
    <>


    <div className="col-md-3" style={{marginLeft:"15px",marginTop:"15px"}}>
    <div className="input-group container" style={{
    display: "flex",
    margin: "0px",
    padding: "0px",
    lineHeight:"0"
}}>
        <div id="search-autocomplete" className="form-outline">
          <input type="text" id="form1"  className="col-md-3" onChange={e=>onChangeHandler(e.target.value)} value={text} className="form-control" onBlur={()=>{
            setSuggestion([])
          },100}  style={{paddingRight:"60px"}}/>
  
        </div>
        <button type="button" className="btn btn-primary">
          <i className="fas fa-search" />
        </button>
      
</div>
    <div className="my-3 suggest" onClick={()=>onSuggestHandler(suggestion.email)} style={{border:"1px solid black"}}>
   {suggestion && suggestion.map((suggest,i)=><div key={i} className="col-md-3 ">{suggest.email}</div>)}

    </div>
<WatchList/>
      </div>
    
    </>
    );
}

export default SearchSection;
