import React, { useState, useEffect,useRef } from 'react';
import axios from 'axios';
// import TextField from '@material-ui/core/TextField';
// import Autocomplete from '@material-ui/lab/Autocomplete';
import { WatchList } from './exportfiles'
function SearchSection() {
  const [serach, setSerach] = useState([]);
  const [text, setText] = useState('')
  const [suggestion, setSuggestion] = useState([])
  useEffect(() => {
    const loadsearch = async () => {
      const response = await axios.get('https://cdn.jsdelivr.net/gh/akabab/superhero-api@0.3.0/api/all.json')
      setSerach(response.data)
    }
    loadsearch()

  }, [])


  // saving
  
    
     
  const onSuggestHandler = (text) => {
    setText(text);
    

    setSuggestion([])
  }

  const onChangeHandler = (text) => {
    let matches = [];
    if (text.length > 0) {
      matches = serach.filter(val => {
        const regex = new RegExp(`${text}`, 'gi');
        return val.name.match(regex)
      })
    }
    console.log('matches', matches)
    setSuggestion(matches)
    setText(text)
   
  }



  const txtInput = useRef(null)
  // database
 
  // database

  
  
    let handelSubmit =(value)=>{
  
  const addwatchlist ={
    watchValues:txtInput.current.value
  }
  
  axios.post('http://localhost:3000//addwatchlist',addwatchlist);
  
setSerach({watchValues:''})
setText([]);
    }
  
  return (
    <>


      <div className="col-md-3" style={{ marginLeft: "15px", marginTop: "15px" }}>
        <div className="input-group container" style={{
          display: "flex",
          margin: "0px",
          padding: "0px",
          lineHeight: "0"
        }}>
          <div id="search-autocomplete" className="form-outline">
            <input type="text"  ref={txtInput} onChange={e => onChangeHandler(e.target.value)} value={text} name="watchValues" onBlur={() => {setTimeout(() => {
              
              setSuggestion([])
            }, 100)}} 
             style={{ paddingRight: "80px",paddingBottom:"17px" }} />

          </div>
          <button type="button" className="btn btn-primary" onClick={handelSubmit}>
            <i className="fas fa-search" />
          </button>

        </div>
        <div className="my-3" style={{ border: "1px solid black",width:"91%",maxHeight:"250px" ,overflowY: "scroll"}}>
          {suggestion && suggestion.map((suggest, i) => <div key={i}  
          onClick={() => onSuggestHandler(suggest.name)} className=" suggest mx-2"><span >{suggest.name}</span></div>)}

        </div>
        <WatchList />
      </div>

    </>
  );
}

export default SearchSection;
