import React, { useState } from 'react';
import { RuleSection } from './exportfiles';
import axios from 'axios';
function NotesSection() {
  const [isShown, setIsShown] = useState(false);
const [input, setInput] = useState({
  title:'',
  desc:''
});

  // database

  let handelChange =(e)=>{
const {name,value} =e.target;
setInput(prevInput=>{
  return {
    ...prevInput,
    [name]:value
  }
})
  }

  let handelSubmit =(event)=>{
event.preventDefault();
const Note ={
  title:input.title,
  desc:input.desc
}

axios.post('http://localhost:3000/note',Note)
  }

  return (
    <>
      <div className="col-md-3">

        <div className="scrollContainer border border-dark" style={{
          // overflowY: "scroll",
          marginTop: "10px",
          padding: "0px",
          marginRight: "-95px"
        }}>

          <div className="title" style={{
            margin: "0", padding: "0px", display: "flex", flexDirection: "row",
            justifyContent: "space-between"
          }}>
            <p style={{
              fontWeight: "500", marginTop: "13px",
              marginBottom: "0px"
            }}>Notes</p>
            <button type="button" className="btn btn-primary-outline addingbtn my-2" data-bs-toggle="modal" data-bs-target="#exampleModal">
              <i className="fas fa-plus"></i>
            </button>
          </div>

          {/* list  */}

          <div className="list-group" style={{ overflowY: "scroll", maxHeight: "310px" }}>
            <div onMouseEnter={() => setIsShown(true)}
              onMouseLeave={() => setIsShown(false)} className=" border border-dark" style={{ margin: "0px 5px 5px 5px" }}>
              {isShown && (<div><i onClick={console.log("hello")} class="fas fa-pen-fancy mx-3"></i>
                <i class="fas fa-trash"></i></div>)} <div className="d-flex w-95 justify-content-between border border-dark" style={{ margin: "5px" }}>
                <h5 className="mb-1">List group item heading</h5>
                <small>3 days ago</small>
              </div>

              <p className="mb-1">Some placeholder content in a paragraph.

              </p>
            </div>

            {/*  */}
            <div className=" border border-dark" style={{ margin: "5px" }}>
              <div className="d-flex w-95 justify-content-between border border-dark" style={{ margin: "5px" }}>
                <h5 className="mb-1">List group item heading</h5>
                <small>3 days ago</small>
              </div>
              <p className="mb-1">Some placeholder content in a paragraph.</p>
            </div>

            {/*  */}
            <div className=" border border-dark" style={{ margin: "5px" }}>
              <div className="d-flex w-95 justify-content-between border border-dark" style={{ margin: "5px" }}>
                <h5 className="mb-1">List group item heading</h5>
                <small>3 days ago</small>
              </div>
              <p className="mb-1">Some placeholder content in a paragraph.</p>
            </div>

            <div className=" border border-dark" style={{ margin: "5px" }}>
              <div className="d-flex w-95 justify-content-between border border-dark" style={{ margin: "5px" }}>
                <h5 className="mb-1">List group item heading</h5>
                <small>3 days ago</small>
              </div>
              <p className="mb-1">Some placeholder content in a paragraph.</p>
            </div>

            <div className=" border border-dark" style={{ margin: "5px" }}>
              <div className="d-flex w-95 justify-content-between border border-dark" style={{ margin: "5px" }}>
                <h5 className="mb-1">List group item heading</h5>
                <small>3 days ago</small>
              </div>
              <p className="mb-1">Some placeholder content in a paragraph.</p>
            </div>

          </div>


          {/* ---------------------------modal----------------------------------------- */}


          <div className="modal fade" id="exampleModal" tabIndex={-1} aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
                  <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
                </div>
                <div className="modal-body">
                  <form>
                    <div className="mb-3">
                      <label htmlFor="exampleInputEmail1" className="form-label">Title</label>
                      <input type="text" name="title" value={input.title} onChange={handelChange} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="exampleInputPassword1" className="form-label">Description</label>
                      <textarea class="form-control" value={input.desc} name="desc" onChange={handelChange}></textarea>
                    </div>


                  </form>

                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                  <button type="button" onClick={handelSubmit} className="btn btn-primary">Add</button>
                </div>
              </div>
            </div>
          </div>

        </div>
        <RuleSection />
      </div>
    </>
  );
}

export default NotesSection;
