import React, { useState, useRef, useEffect,useContext } from 'react';
import { RuleSection } from './exportfiles';
import Notescontext from './context/Notecontext'
import axios from 'axios';
function NotesSection(props) {
  const [isShown, setIsShown] = useState(false);
  const [input, setInput] = useState({
    title: '',
    desc: ''
  });

  const ref = useRef(null)
  // database

  let handelChange = (e) => {
    const { name, value } = e.target;
    setInput(prevInput => {
      return {
        ...prevInput,
        [name]: value
      }
    })
  }

  let handelSubmit = (event) => {

    const Note = {
      title: input.title,
      desc: input.desc
    }

    axios.post('http://localhost:3000/note', Note);
    ref.current.click()
    setInput({ title: '', desc: '' })
  }


  // fetch all data

  const [notes, setNotes] = useState([{ title: '', desc: '', date: '' }]);

  useEffect(() => {
    fetch('/savednote').then(res => {
      if (res.ok) {
        return res.json()
      }
    }).then(jsonRes => setNotes(jsonRes))
  })

  // update note

  const context = useContext(Notescontext)
  const [uinput, setUinput] = useState({utitle:"",
  udesc:""});
  const  updateNote  = context;
  const updateNoteValue=(newval)=>{
    
    setUinput({id:newval._id,utitle: newval.title, udesc: newval.desc})
  }
  const handelUpdateSubmit = (e) => {
    updateNote(uinput.id,uinput.utitle,uinput.udesc);
    ref.current.click()
  }
  const onUpchange = (e) => {
    setUinput({ ...uinput, [e.target.name]: e.target.value })
  }


  // delete
  const deleteNote=(id, e)=>{  
    axios.delete(`http://localhost:5000/remove/${id}`)  
      .then(res => {  
        console.log(res);  
        console.log(res.data);  
    
        const posts =input.filter(item => {return item._id !== id});  
        setInput( posts ); 
      })  
     
  }  
  return (
    <>
      <div className="col-md-3">

        <div className="scrollContainer border border-dark" style={{
          // overflowY: "scroll",
          marginTop: "10px",
          padding: "0px",
          marginRight: "-95px"
        }}>

          <div className="title" style={{
            margin: "0", padding: "0px", display: "flex", flexDirection: "row",
            justifyContent: "space-between"
          }}>
            <p style={{
              fontWeight: "500", marginTop: "13px",
              marginBottom: "0px"
            }}>Notes</p>
            <button type="button" className="btn btn-primary-outline addingbtn my-2" data-bs-toggle="modal" data-bs-target="#exampleModal">
              <i className="fas fa-plus"></i>
            </button>
          </div>

          {/* list  */}

          <div className="list-group" style={{ overflowY: "scroll", maxHeight: "310px" }}>


            {notes.map(note => (<div onMouseEnter={() => setIsShown(true)}
              onMouseLeave={() => setIsShown(false)} className=" border border-dark" style={{ margin: "0px 5px 5px 5px" }}>
              {isShown && (<div>
                <button type="button" className="btn btn-primary-outline addingbtn my-2" data-bs-toggle="modal" data-bs-target="#update">
                <i onClick={console.log(input._id)}  class="fas fa-pen-fancy mx-3"></i>
            </button>
               
                <i class="fas fa-trash"  onClick={() => {
                deleteNote(input._id)
              }} style={{ cursor: "pointer" }}></i></div>)} <div className="d-flex w-95 justify-content-between border border-dark" style={{ margin: "5px" }}>
                <h5 className="mb-1">{note.title}</h5>
                <small>{note.date}</small>
              </div>

              <p className="mb-1 mx-2">{note.desc}
              </p>
            </div>))}



          </div>


          {/* ---------------------------modal----------------------------------------- */}


          <div className="modal fade" id="exampleModal" tabIndex={-1} aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
                  <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
                </div>
                <div className="modal-body">
                  <form>
                    <div className="mb-3">
                      <label htmlFor="exampleInputEmail1" className="form-label">Title</label>
                      <input type="text" name="title" value={input.title} onChange={handelChange} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="exampleInputPassword1" className="form-label">Description</label>
                      <textarea class="form-control" value={input.desc} name="desc" onChange={handelChange}></textarea>
                    </div>


                  </form>

                </div>
                <div className="modal-footer">
                  <button type="button" ref={ref} className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                  <button type="button" onClick={handelSubmit} className="btn btn-primary">Update Note</button>
                </div>
              </div>
            </div>
          </div>











          {/* edit modal */}
          <div className="modal fade" id="update" tabIndex={-1} aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalLabel">Update Note</h5>
                  <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
                </div>
                <div className="modal-body">
                  <form>
                    <div className="mb-3">
                      <label htmlFor="exampleInputEmail1" className="form-label"> Update Title</label>
                      <input type="text" name="utitle" onChange={onUpchange} value={uinput.utitle}  className="form-control"  />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="exampleInputPassword1" className="form-label">Update Description</label>
                      <textarea class="form-control" onChange={onUpchange} value={uinput.udesc} name="udesc" ></textarea>
                    </div>


                  </form>

                </div>
                <div className="modal-footer">
                  <button type="button" ref={ref} className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                  <button type="button" onClick={handelUpdateSubmit} className="btn btn-primary">Add</button>
                </div>
              </div>
            </div>
          </div>


        </div>


        {/* delete note */}

        <RuleSection />
      </div>
    </>
  );
}

export default NotesSection;
