import React, { useState,useEffect} from 'react';
import axios from 'axios';
// import TextField from '@material-ui/core/TextField';
// import Autocomplete from '@material-ui/lab/Autocomplete';
import {WatchList} from './exportfiles'
function SearchSection() {
    
  const [search,setSearch]= useState([]);
  const [val, setVal] = useState('');
  const [suggest, setSuggest] = useState([]);
  useEffect(() => {
    const loadData=async ()=>{
      const response = await axios("http://localhost:5000/watchlist");
      console.log(response.data);
      setSearch(response.data);
    };
  
    loadData();
  }, []);

// suggestion


  // onchnage
  const onchnageData = (val)=>{
    var matches =[];
  if(val.length>0){
    // let newVal =response.watchValues
    matches = search.filter(watchValues=>{
      const filterAlgo = new RegExp(`${val}`,"gi");
      return watchValues.match(filterAlgo)
    })
  }
  console.log("match",matches)
  setSuggest(matches);
setVal(val);
  }
  
  return (
    <>


    <div className="col-md-3">
    <input   onChange={e=>onchnageData(e.target.value)}  value={val} />

<WatchList/>
      </div>
    
    </>
    );
}

export default SearchSection;
