import React,{ useState,useEffect,useRef}  from 'react';
function RuleSection() {
const [rule, setRule] = useState([{
  rules:''
}]);

const ref = useRef(null);
const selectHandel =()=>{
  ref.current.click()
}

useEffect(() => {
  fetch('/rules').then(res=>{
    if(res.ok){
      return res.json()
    }
  }).then(jsonRes=>setRule(jsonRes))
})


  return (
    <>
    <div className="divstyle">
    <div className="scrollContainer border border-dark" style={{
    // overflowY: "scroll",
    marginTop: "10px",
    padding: "0px",
    marginRight: "-95px"}}>

      <div className="title" style={{margin:"0px",padding:"0px",display:"flex",flexDirection: "row",
    justifyContent: "space-between"}}>
        <p style={{fontWeight:"500",marginTop: "10px",
    marginBottom: "0px"}}>Rules</p>
    <div>

        <i className="fas fa-check" onClick={selectHandel} style={{margin:"inherit"}} ></i>
        <button type="button" className="btn btn-primary-outline addingbtn my-2" data-bs-toggle="modal" data-bs-target="#exampleModal">
        <i className="fas fa-plus " style={{margin:"inherit"}}></i>
      </button>
        <i className="fas fa-trash mr-2" ></i>
    </div>
      </div>
      <div className="list-group border border-dark " style={{ overflowY: "scroll",maxHeight: "260px",margin:"0px 5px 5px 5px"}}>
        {rule.map(Srules=>(<label className="list-group-item">
          <input className="form-check-input me-1" ref={ref} type="checkbox" defaultValue />
          {Srules.rules}
        </label>))}
      
      </div>
      </div>
</div>
  </>);
}

export default RuleSection;
