import React, { useState,useEffect} from 'react';
import axios from 'axios';
// import TextField from '@material-ui/core/TextField';
// import Autocomplete from '@material-ui/lab/Autocomplete';
import {WatchList} from './exportfiles'
function SearchSection() {
    const [watchvalue, setWatchvalue] = useState([]);
  const [text, setText] = useState('');
  const [suggestion, setSuggestion] = useState([]);

    const fetchData = () => {
      fetch("http://localhost:5000/watchlist")
        .then(response => {
          return response.json()
        })
        .then(data => {
          setWatchvalue(data)
        })
    }
  
    useEffect(() => {
      fetchData()
    }, [])
 
// suggestion


  // onchnage

  const onChangeHandler=(text)=>{
    let matches=[]
    if(text.length>0){
      matches = watchvalue.filter(watchlists=>{
        const regex = new RegExp(`${text}`,"gi");
        watchlists.id.watchValues(regex)
      })
    }
    console.log("matches", matches)
    setSuggestion(matches)
    setText(text);
  }
 
  return (
    <>


    <div className="col-md-3">

    <div className="input-group">
  <div className="form-outline">
    <input type="text" id="form1" className="form-control" onChange={e=>onChangeHandler(e.target.value)} value={text}/>
    <span className="input-group-text border-0" id="search-addon">
    <i className="fas fa-search"></i>
  </span>
  </div>
</div>
    <div>
    {watchvalue.length > 0 && (
        <ul>
          {watchvalue.map(val => (
            <li key={val.id}>{val.watchValues}</li>
          ))}
        </ul>
      )}
    </div>
<WatchList/>
      </div>
    
    </>
    );
}

export default SearchSection;
