import React, { useState,useEffect} from 'react';
import axios from 'axios';
// import TextField from '@material-ui/core/TextField';
// import Autocomplete from '@material-ui/lab/Autocomplete';
import {WatchList} from './exportfiles'
function SearchSection() {
    
  const [search,setSearch]= useState([]);
  const [val, setVal] = useState('');
  const [suggest, setSuggest] = useState([]);
  useEffect(() => {
    const loadData=async ()=>{
      const response = await axios("http://localhost:5000/watchlist");
      console.log(response.data);
      setSearch(response.data.data);
    };
  
    loadData();
  }, []);

// suggestion


  // onchnage
  const onchnageData = (val)=>{
    var matches =[];
  if(val.length>0){
    matches = search.filter(watchValues=>{
      const filterAlgo = new RegExp(`${val}`,"gi");
      return watchValues.match(filterAlgo)
    })
  }
  console.log("match",matches)
  setSuggest(matches);
setVal(val);
  }
  
  return (
    <>


    <div className="col-md-3">
    <div className="input-group">
        <div className="form-outline">
          <input type="search" id="form1" onChange={e=>onchnageData(e.target.value)}  Value={val} className="form-control" />
          <label className="form-label" htmlFor="form1">Search</label>
        </div>
        <button type="button" className="btn btn-primary">
          <i className="fas fa-search" />
        </button>
      </div>

<WatchList/>
    </div>
    </>
    );
}

export default SearchSection;
