import React, { useState, useEffect,useRef } from 'react';
import axios from 'axios';
// import TextField from '@material-ui/core/TextField';
// import Autocomplete from '@material-ui/lab/Autocomplete';
import { WatchList } from './exportfiles'
function SearchSection() {
  
    const [display, setDisplay] = useState(false);
    const [options, setOptions] = useState([]);
    const [search, setSearch] = useState("");
    const wrapperRef = useRef(null);
  
    useEffect(() => {
      const pokemon = [];
      const promises = new Array(20)
        .fill()
        .map((v, i) => fetch(`https://pokeapi.co/api/v2/pokemon-form/${i + 1}`));
      Promise.all(promises).then(pokemonArr => {
        return pokemonArr.map(value =>
          value
            .json()
            .then(({ name, sprites: { front_default: sprite } }) =>
              pokemon.push({ name, sprite })
            )
        );
      });
      setOptions(pokemon);
    }, []);
  
    useEffect(() => {
      window.addEventListener("mousedown", handleClickOutside);
      return () => {
        window.removeEventListener("mousedown", handleClickOutside);
      };
    });
  
    const handleClickOutside = event => {
      const { current: wrap } = wrapperRef;
      if (wrap && !wrap.contains(event.target)) {
        setDisplay(false);
      }
    };
    const txtInput = useRef(null)
    const updatePokeDex = poke => {
      const addwatchlist ={
        watchValues:txtInput.current.value
      }
      
  axios.post('http://localhost:3000//addwatchlist',addwatchlist);
      setSearch(poke);
      setDisplay(false);
    };
  
    return (
      
      <div className="col-md-3" style={{ marginLeft: "15px", marginTop: "15px" }}>
      <div ref={wrapperRef} className="flex-container flex-column pos-rel">
        <input
          id="auto"
          onClick={() => setDisplay(!display)}
          placeholder="Type to search"
          value={search}
          onChange={event => setSearch(event.target.value)}
          aria-label="Search"
          
          
          style={{ paddingRight: "80px",paddingBottom:"17px" }}
        />
           <button type="button" className="btn btn-primary">
            <i className="fas fa-search" />
          </button>
        {display && (
          <div className="my-3" style={{ border: "1px solid black",width:"91%",maxHeight:"250px" ,overflowY: "scroll"}}>
            {options
              .filter(({ name }) => name.indexOf(search.toLowerCase()) > -1)
              .map((value, i) => {
                return (
                  <div
                    onClick={() => updatePokeDex(value.name)}
                    className="option"
                    key={i}
                    tabIndex="0"
                    
          ref={txtInput}
                  >
                    <span>{value.name}</span>
                   
                  </div>
                );
              })}
          </div>
        )}
      </div>
      <WatchList/>
      </div>
    );
  };
  
  
   


export default SearchSection;
